
public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	// intialize
	int x=0;
		
	while (x<20) //condition
		{
			System.out.println(x);
			x+=2;
			// you can also use x++
		}
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
